import { useCallback, useEffect, useMemo, useRef, useState } from 'react';

import MediaResultCard from '../components/MediaResultCard.jsx';
import StatCard from '../components/StatCard.jsx';
import useSnapshotPreview from '../hooks/useSnapshotPreview.js';
import { fetchMediaHistory, fetchModelMetadata } from '../services/mediaService.js';

const HERO_TITLE = 'Panel maestro de emociones';
const HERO_SUBTITLE = 'Explora cómo se comportan las emociones detectadas y filtra los registros según el sentimiento dominante.';
const ALL_EMOTIONS_LABEL = 'Todas las emociones';
const EMOTION_TRANSLATIONS = {
  angry: 'Enojo',
  disgust: 'Desagrado',
  fear: 'Miedo',
  happy: 'Felicidad',
  neutral: 'Neutral',
  sad: 'Tristeza',
  surprise: 'Sorpresa',
};

const getEmotionLabel = (label) => {
  if (!label) {
    return '';
  }
  const key = label.toString().trim().toLowerCase();
  return EMOTION_TRANSLATIONS[key] || label;
};

function EmotionPreviewFilter({
  label,
  displayLabel,
  total,
  snapshot,
  isActive,
  onSelect,
  className = '',
  showTotal = true,
}) {
  const { previewUrl, fallbackUrl, state } = useSnapshotPreview(snapshot);
  const imageUrl = previewUrl || fallbackUrl;
  const friendlyLabel = displayLabel || label;
  const placeholderLetter = friendlyLabel ? friendlyLabel.substring(0, 1).toUpperCase() : '?';
  const totalLabel = total > 0 ? `${total} ${total === 1 ? 'captura' : 'capturas'}` : 'Sin capturas';

  return (
    <button
      type="button"
      className={`emotion-preview-card ${isActive ? 'is-active' : ''} ${className}`.trim()}
      onClick={onSelect}
    >
      <span
        className={`emotion-preview-thumb ${state === 'loading' ? 'is-loading' : ''} ${imageUrl ? '' : 'is-placeholder'}`}
        style={imageUrl ? { backgroundImage: `url(${imageUrl})` } : undefined}
      >
        {!imageUrl && placeholderLetter}
      </span>
      <span className="emotion-preview-label">
        <strong>{friendlyLabel}</strong>
        {showTotal ? <small>{totalLabel}</small> : null}
      </span>
    </button>
  );
}

function Dashboard() {
  const [overviewPool, setOverviewPool] = useState([]);
  const [latestResults, setLatestResults] = useState([]);
  const [history, setHistory] = useState([]);
  const [activeEmotion, setActiveEmotion] = useState('all');
  const [selectorOpen, setSelectorOpen] = useState(false);
  const [availableEmotions, setAvailableEmotions] = useState([]);
  const [modelLabels, setModelLabels] = useState([]);
  const [historyLoading, setHistoryLoading] = useState(false);
  const [error, setError] = useState('');
  const selectorRef = useRef(null);

  const loadOverview = useCallback(async () => {
    try {
      const response = await fetchMediaHistory({ limit: 30 });
      const items = response.items || [];
      setOverviewPool(items);
      setLatestResults(items.slice(0, 4));
      setAvailableEmotions(response.filters?.available_emotions || []);
    } catch (loadError) {
      setError(loadError.response?.data?.message || 'No se pudo cargar el dashboard de emociones.');
    }
  }, []);

  const loadMetadata = useCallback(async () => {
    try {
      const metadata = await fetchModelMetadata();
      setModelLabels(metadata.labels || []);
    } catch (metadataError) {
      console.error('No se pudo obtener etiquetas del modelo', metadataError);
    }
  }, []);

  const loadHistory = useCallback(async () => {
    setHistoryLoading(true);
    setError('');
    try {
      const response = await fetchMediaHistory({
        limit: 18,
        emotion: activeEmotion,
      });
      setHistory(response.items || []);
      setAvailableEmotions(response.filters?.available_emotions || []);
    } catch (loadError) {
      setError(loadError.response?.data?.message || 'No se pudo filtrar el historial.');
    } finally {
      setHistoryLoading(false);
    }
  }, [activeEmotion]);

  useEffect(() => {
    loadOverview();
    loadMetadata();
  }, [loadOverview, loadMetadata]);

  useEffect(() => {
    loadHistory();
  }, [loadHistory]);

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (!selectorRef.current || selectorRef.current.contains(event.target)) {
        return;
      }
      setSelectorOpen(false);
    };
    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  const stats = useMemo(() => {
    const total = overviewPool.length;
    const emotionCounter = overviewPool.reduce((acc, item) => {
      if (!item.dominant_emotion) {
        return acc;
      }
      const key = item.dominant_emotion;
      acc[key] = (acc[key] || 0) + 1;
      return acc;
    }, {});
    return {
      total,
      emotionCounter,
    };
  }, [overviewPool]);

  const filteredOverview = useMemo(() => {
    if (activeEmotion === 'all') {
      return overviewPool;
    }
    return overviewPool.filter((item) => item.dominant_emotion === activeEmotion);
  }, [overviewPool, activeEmotion]);

  const filteredLatestResults = useMemo(() => {
    if (activeEmotion === 'all') {
      return latestResults;
    }
    return latestResults.filter((item) => item.dominant_emotion === activeEmotion);
  }, [latestResults, activeEmotion]);

  const filteredHistoryRecords = useMemo(() => {
    if (activeEmotion === 'all') {
      return history;
    }
    return history.filter((item) => item.dominant_emotion === activeEmotion);
  }, [history, activeEmotion]);

  const averageConfidence = filteredOverview.length
    ? filteredOverview.reduce((acc, item) => acc + (item.confidence || 0), 0) / filteredOverview.length
    : 0;

  const sourceBreakdown = useMemo(() => {
    const pool = filteredOverview.length ? filteredOverview : overviewPool;
    return pool.reduce((acc, item) => {
      const key = item.source_type || 'otros';
      acc[key] = (acc[key] || 0) + 1;
      return acc;
    }, {});
  }, [filteredOverview, overviewPool]);

  const sourceEntries = Object.entries(sourceBreakdown).sort((a, b) => b[1] - a[1]);
  const topSources = sourceEntries.slice(0, 3);

  const lastUpdated = filteredHistoryRecords[0]?.created_at || overviewPool[0]?.created_at;

  const distributionData = useMemo(() => {
    const base = stats.total || 1;
    return (modelLabels.length > 0 ? modelLabels : Object.keys(stats.emotionCounter || {}))
      .filter(Boolean)
      .map((label) => {
        const count = stats.emotionCounter?.[label] || 0;
        const percentage = Math.round((count / base) * 100);
        return {
          label,
          displayLabel: getEmotionLabel(label),
          count,
          percentage,
        };
      })
      .filter((item) => item.count > 0)
      .sort((a, b) => b.count - a.count);
  }, [stats, modelLabels]);

  const emotionSnapshots = useMemo(() => {
    const snapshotMap = new Map();
    [...overviewPool, ...history].forEach((item) => {
      if (!item?.dominant_emotion || !item.snapshot_url) {
        return;
      }
      if (!snapshotMap.has(item.dominant_emotion)) {
        snapshotMap.set(item.dominant_emotion, item.snapshot_url);
      }
    });
    return snapshotMap;
  }, [overviewPool, history]);

  const activeEmotionLabel = activeEmotion === 'all' ? ALL_EMOTIONS_LABEL : getEmotionLabel(activeEmotion);
  const heroDescription = activeEmotion === 'all'
    ? HERO_SUBTITLE
    : `Mostrando capturas clasificadas como ${activeEmotionLabel}.`;
  const activeEmotionCount = activeEmotion === 'all'
    ? stats.total
    : stats.emotionCounter?.[activeEmotion] || 0;
  const activeEmotionCountLabel = activeEmotionCount > 0
    ? `${activeEmotionCount} ${activeEmotionCount === 1 ? 'captura' : 'capturas'}`
    : 'Sin capturas registradas';
  const historyBadgeLabel = historyLoading
    ? 'Actualizando…'
    : `${filteredHistoryRecords.length} ${filteredHistoryRecords.length === 1 ? 'registro' : 'registros'}${activeEmotion !== 'all' ? ` · ${activeEmotionLabel}` : ''}`;

  const emotionBadges = (modelLabels.length > 0
    ? modelLabels
    : Array.from(new Set([...availableEmotions, ...Object.keys(stats.emotionCounter || {})])))
    .filter(Boolean)
    .map((label) => ({
      label,
      displayLabel: getEmotionLabel(label),
      total: stats.emotionCounter?.[label] || 0,
      snapshot: emotionSnapshots.get(label) || null,
    }));

  const handleEmotionSelect = useCallback((label) => {
    setActiveEmotion(label);
    setSelectorOpen(false);
  }, []);

  const summaryCards = [
    { label: 'Muestras registradas', value: stats.total },
    { label: 'Promedio de confianza', value: `${(averageConfidence * 100).toFixed(1)}%` },
    { label: 'Emociones presentes', value: Object.keys(stats.emotionCounter || {}).length },
    { label: 'Última actualización', value: lastUpdated ? new Date(lastUpdated).toLocaleTimeString() : 'N/D' },
  ];

  return (
    <section className="app-container emotion-dashboard">
      <div className="emotion-hero card">
        <div className="emotion-hero-head">
          <div>
            <p className="page-subtitle">Dashboard emociones</p>
            <h1>{HERO_TITLE}</h1>
            <p>{heroDescription}</p>
          </div>
          <div className="emotion-hero-meta">
            <span>{activeEmotionLabel}</span>
            <small>{activeEmotionCountLabel}</small>
          </div>
        </div>
        <div className="emotion-summary-grid">
          {summaryCards.map(({ label, value }) => (
            <StatCard key={label} label={label} value={value} />
          ))}
        </div>
        <div className="emotion-filter-strip" ref={selectorRef}>
          <EmotionPreviewFilter
            label="all"
            displayLabel={ALL_EMOTIONS_LABEL}
            total={stats.total}
            snapshot={null}
            isActive={activeEmotion === 'all'}
            onSelect={() => handleEmotionSelect('all')}
            className="is-persistent"
          />
          <div className={`emotion-filter-scroll ${selectorOpen ? 'is-open' : ''}`}>
            <button
              type="button"
              className="emotion-filter-toggle"
              onClick={() => setSelectorOpen((prev) => !prev)}
            >
              {selectorOpen ? 'Cerrar catálogo' : 'Ver todas las emociones'}
            </button>
            <div className="emotion-filter-grid">
              {emotionBadges.map(({ label, displayLabel, total, snapshot }) => (
                <EmotionPreviewFilter
                  key={label}
                  label={label}
                  displayLabel={displayLabel}
                  total={total}
                  snapshot={snapshot}
                  isActive={activeEmotion === label}
                  onSelect={() => handleEmotionSelect(label)}
                />
              ))}
            </div>
          </div>
        </div>
      </div>

      <div className="emotion-panels-grid">
        <div className="emotion-panel card">
          <div className="emotion-panel-header">
            <div>
              <p className="emotion-panel-kicker">Pulso inmediato</p>
              <h2 className="emotion-section-title">Últimas capturas filtradas</h2>
            </div>
            <span className="emotion-chip chip-live">En vivo</span>
          </div>
          {filteredLatestResults.length === 0 ? (
            <p className="emotion-dashboard-empty">
              {activeEmotion === 'all'
                ? 'Aún no hay registros recientes.'
                : `No hay registros recientes para ${activeEmotionLabel}.`}
            </p>
          ) : (
            <div className="emotion-latest-grid">
              {filteredLatestResults.map((item) => (
                <MediaResultCard key={`latest-${item.id}`} item={item} />
              ))}
            </div>
          )}
        </div>

        <div className="emotion-panel card emotion-distribution-card">
          <div className="emotion-panel-header">
            <div>
              <p className="emotion-panel-kicker">Dominante vs. contexto</p>
              <h2 className="emotion-section-title">Distribución por emoción</h2>
            </div>
            <span className="emotion-chip">{stats.total} muestras</span>
          </div>
          {distributionData.length === 0 ? (
            <p className="emotion-dashboard-empty">Sin datos para graficar aún.</p>
          ) : (
            <ul className="emotion-distribution-list">
              {distributionData.map(({ label, displayLabel, count, percentage }) => (
                <li key={label}>
                  <div className="emotion-distribution-row">
                    <div>
                      <strong>{displayLabel}</strong>
                      <small>{count} {count === 1 ? 'captura' : 'capturas'}</small>
                    </div>
                    <span>{percentage}%</span>
                  </div>
                  <div className="emotion-distribution-bar">
                    <span style={{ width: `${percentage}%` }} />
                  </div>
                </li>
              ))}
            </ul>
          )}
          <div className="emotion-aux-grid">
            <div>
              <p className="emotion-panel-kicker">Canales principales</p>
              {topSources.length === 0 ? (
                <p className="emotion-dashboard-empty">Aún no registramos canales para este filtro.</p>
              ) : (
                <ul className="emotion-channel-list">
                  {topSources.map(([label, count]) => (
                    <li key={label}>
                      <span>{label}</span>
                      <strong>{count}</strong>
                    </li>
                  ))}
                </ul>
              )}
            </div>
            <div>
              <p className="emotion-panel-kicker">Resumen del filtro</p>
              <div className="emotion-panel-summary">
                <p>
                  Total de muestras seleccionadas: <strong>{filteredOverview.length}</strong>
                </p>
                <p>
                  Confianza media: <strong>{(averageConfidence * 100).toFixed(1)}%</strong>
                </p>
                <p>
                  Última actualización: <strong>{lastUpdated ? new Date(lastUpdated).toLocaleString() : 'Pendiente'}</strong>
                </p>
              </div>
            </div>
          </div>
        </div>
      </div>

      <div className="emotion-panel card">
        <div className="emotion-panel-header">
          <div>
            <p className="emotion-panel-kicker">Historial almacenado</p>
            <h2 className="emotion-section-title">Analíticas filtradas</h2>
          </div>
          <span className="emotion-chip">{historyBadgeLabel}</span>
        </div>
        {error && <p className="media-error">{error}</p>}
        {historyLoading ? (
          <p className="emotion-dashboard-empty">Cargando historial...</p>
        ) : filteredHistoryRecords.length === 0 ? (
          <p className="emotion-dashboard-empty">
            {activeEmotion === 'all'
              ? 'No hay registros almacenados aún.'
              : `No hay coincidencias para ${activeEmotionLabel} en el historial.`}
          </p>
        ) : (
          <div className="emotion-history-grid">
            {filteredHistoryRecords.map((item) => (
              <MediaResultCard key={`history-${item.id}`} item={item} />
            ))}
          </div>
        )}
      </div>
    </section>
  );
}

export default Dashboard;
