import { useCallback, useEffect, useMemo, useRef, useState } from 'react';

import MediaResultCard from '../components/MediaResultCard.jsx';
import useSnapshotPreview from '../hooks/useSnapshotPreview.js';
import { fetchMediaHistory, fetchModelMetadata } from '../services/mediaService.js';

const HERO_TITLE = 'Filtra por emociones';
const HERO_SUBTITLE = 'Selecciona una emoción del modelo para visualizar ejemplos alineados y consistentes.';
const ALL_EMOTIONS_LABEL = 'Todas las emociones';
const EMOTION_TRANSLATIONS = {
  angry: 'Enojo',
  disgust: 'Desagrado',
  fear: 'Miedo',
  happy: 'Felicidad',
  neutral: 'Neutral',
  sad: 'Tristeza',
  surprise: 'Sorpresa',
};

const getEmotionLabel = (label) => {
  if (!label) {
    return '';
  }
  const key = label.toString().trim().toLowerCase();
  return EMOTION_TRANSLATIONS[key] || label;
};

function EmotionPreviewFilter({
  label,
  displayLabel,
  total,
  snapshot,
  isActive,
  onSelect,
  className = '',
  showTotal = true,
}) {
  const { previewUrl, fallbackUrl, state } = useSnapshotPreview(snapshot);
  const imageUrl = previewUrl || fallbackUrl;
  const friendlyLabel = displayLabel || label;
  const placeholderLetter = friendlyLabel ? friendlyLabel.substring(0, 1).toUpperCase() : '?';
  const totalLabel = total > 0 ? `${total} ${total === 1 ? 'captura' : 'capturas'}` : 'Sin capturas';

  return (
    <button
      type="button"
      className={`emotion-preview-card ${isActive ? 'is-active' : ''} ${className}`.trim()}
      onClick={onSelect}
    >
      <span
        className={`emotion-preview-thumb ${state === 'loading' ? 'is-loading' : ''} ${imageUrl ? '' : 'is-placeholder'}`}
        style={imageUrl ? { backgroundImage: `url(${imageUrl})` } : undefined}
      >
        {!imageUrl && placeholderLetter}
      </span>
      <span className="emotion-preview-label">
        <strong>{friendlyLabel}</strong>
        {showTotal ? <small>{totalLabel}</small> : null}
      </span>
    </button>
  );
}

function Dashboard() {
  const [overviewPool, setOverviewPool] = useState([]);
  const [latestResults, setLatestResults] = useState([]);
  const [history, setHistory] = useState([]);
  const [activeEmotion, setActiveEmotion] = useState('all');
  const [selectorOpen, setSelectorOpen] = useState(false);
  const [availableEmotions, setAvailableEmotions] = useState([]);
  const [modelLabels, setModelLabels] = useState([]);
  const [historyLoading, setHistoryLoading] = useState(false);
  const [error, setError] = useState('');
  const selectorRef = useRef(null);

  const loadOverview = useCallback(async () => {
    try {
      const response = await fetchMediaHistory({ limit: 30 });
      const items = response.items || [];
      setOverviewPool(items);
      setLatestResults(items.slice(0, 4));
      setAvailableEmotions(response.filters?.available_emotions || []);
    } catch (loadError) {
      setError(loadError.response?.data?.message || 'No se pudo cargar el dashboard de emociones.');
    }
  }, []);

  const loadMetadata = useCallback(async () => {
    try {
      const metadata = await fetchModelMetadata();
      setModelLabels(metadata.labels || []);
    } catch (metadataError) {
      console.error('No se pudo obtener etiquetas del modelo', metadataError);
    }
  }, []);

  const loadHistory = useCallback(async () => {
    setHistoryLoading(true);
    setError('');
    try {
      const response = await fetchMediaHistory({
        limit: 18,
        emotion: activeEmotion,
      });
      setHistory(response.items || []);
      setAvailableEmotions(response.filters?.available_emotions || []);
    } catch (loadError) {
      setError(loadError.response?.data?.message || 'No se pudo filtrar el historial.');
    } finally {
      setHistoryLoading(false);
    }
  }, [activeEmotion]);

  useEffect(() => {
    loadOverview();
    loadMetadata();
  }, [loadOverview, loadMetadata]);

  useEffect(() => {
    loadHistory();
  }, [loadHistory]);

  useEffect(() => {
    const handleClickOutside = (event) => {
      if (!selectorRef.current) {
        return;
      }
      if (!selectorRef.current.contains(event.target)) {
        setSelectorOpen(false);
      }
    };
    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, []);

  const stats = useMemo(() => {
    const total = overviewPool.length;
    const emotionCounter = overviewPool.reduce((acc, item) => {
      if (!item.dominant_emotion) {
        return acc;
      }
      const key = item.dominant_emotion;
      acc[key] = (acc[key] || 0) + 1;
      return acc;
    }, {});
    const sortedEmotions = Object.entries(emotionCounter).sort((a, b) => b[1] - a[1]);
    const topEmotion = sortedEmotions[0];

    return {
      total,
      emotionCounter,
    };
  }, [overviewPool]);

  const emotionSnapshots = useMemo(() => {
    const snapshotMap = new Map();
    [...overviewPool, ...history].forEach((item) => {
      if (!item?.dominant_emotion || !item.snapshot_url) {
        return;
      }
      if (!snapshotMap.has(item.dominant_emotion)) {
        snapshotMap.set(item.dominant_emotion, item.snapshot_url);
      }
    });
    return snapshotMap;
  }, [overviewPool, history]);

  const historyBadgeLabel = historyLoading
    ? 'Actualizando…'
    : `${history.length} resultados${activeEmotion !== 'all' ? ` · ${activeEmotion}` : ''}`;
  const emotionBadges = (modelLabels.length > 0
    ? modelLabels
    : Array.from(new Set([...availableEmotions, ...Object.keys(stats.emotionCounter || {})])))
    .filter(Boolean)
    .map((label) => ({
      label,
      displayLabel: getEmotionLabel(label),
      total: stats.emotionCounter?.[label] || 0,
      snapshot: emotionSnapshots.get(label) || null,
    }));
  const heroDescription = activeEmotion === 'all'
    ? HERO_SUBTITLE
    : `Mostrando capturas clasificadas como ${activeEmotion}.`;
  const filteredLatestResults = activeEmotion === 'all'
    ? latestResults
    : latestResults.filter((item) => item.dominant_emotion === activeEmotion);
  const activeEmotionLabel = activeEmotion === 'all' ? 'Todas las emociones' : activeEmotion;
  const activeEmotionCount = activeEmotion === 'all'
    ? stats.total
    : stats.emotionCounter?.[activeEmotion] || 0;
  const handleEmotionSelect = useCallback((label) => {
    setActiveEmotion(label);
    setSelectorOpen(false);
  }, []);

  return (
    <section className="app-container emotion-dashboard">
      <div className="emotion-dashboard-hero minimal">
        <div className="emotion-hero-copy">
          <p className="page-subtitle">Dashboard emociones</p>
          <h1>{HERO_TITLE}</h1>
          <p>{heroDescription}</p>
        </div>
        <div className="emotion-selector-control" ref={selectorRef}>
          <button
            type="button"
            className={`emotion-selector-trigger ${selectorOpen ? 'is-open' : ''}`}
            onClick={() => setSelectorOpen((prev) => !prev)}
          >
            <div>
              <span>Emoción seleccionada</span>
              <strong>{activeEmotionLabel}</strong>
            </div>
            <div className="emotion-selector-meta">
              <small>{activeEmotionCount} registros</small>
              <span className="emotion-selector-caret" aria-hidden />
            </div>
          </button>
          <div className={`emotion-selector-menu ${selectorOpen ? 'is-open' : ''}`}>
            <div className="emotion-selector-scroll">
              <EmotionPreviewFilter
                label="Todas las emociones"
                total={stats.total}
                snapshot={null}
                isActive={activeEmotion === 'all'}
                onSelect={() => handleEmotionSelect('all')}
                className="is-menu"
              />
              {emotionBadges.map(({ label, total, snapshot }) => (
                <EmotionPreviewFilter
                  key={label}
                  label={label}
                  total={total}
                  snapshot={snapshot}
                  isActive={activeEmotion === label}
                  onSelect={() => handleEmotionSelect(label)}
                  className="is-menu"
                />
              ))}
            </div>
          </div>
        </div>
      </div>

      <div className="emotion-panel card">
        <div className="emotion-panel-header">
          <div>
            <p className="emotion-panel-kicker">Pulso inmediato</p>
            <h2 className="emotion-section-title">Últimos resultados</h2>
          </div>
          <span className="emotion-chip chip-live">En vivo</span>
        </div>
        {filteredLatestResults.length === 0 ? (
          <p className="emotion-dashboard-empty">
            {activeEmotion === 'all'
              ? 'Aún no hay registros recientes.'
              : `No hay registros recientes para ${activeEmotion}.`}
          </p>
        ) : (
          <div className="emotion-latest-grid">
            {filteredLatestResults.map((item) => (
              <MediaResultCard key={`latest-${item.id}`} item={item} />
            ))}
          </div>
        )}
      </div>

      <div className="emotion-panel card">
        <div className="emotion-panel-header">
          <div>
            <p className="emotion-panel-kicker">Historial almacenado</p>
            <h2 className="emotion-section-title">Analíticas filtradas</h2>
          </div>
          <span className="emotion-chip">{historyBadgeLabel}</span>
        </div>
        {error && <p className="media-error">{error}</p>}
        {historyLoading ? (
          <p className="emotion-dashboard-empty">Cargando historial...</p>
        ) : history.length === 0 ? (
          <p className="emotion-dashboard-empty">
            {activeEmotion === 'all'
              ? 'No hay registros almacenados aún.'
              : `No hay coincidencias para ${activeEmotion} en el historial.`}
          </p>
        ) : (
          <div className="emotion-history-grid">
            {history.map((item) => (
              <MediaResultCard key={`history-${item.id}`} item={item} />
            ))}
          </div>
        )}
      </div>
    </section>
  );
}

export default Dashboard;
