import { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';

import useAuth from '../hooks/useAuth.js';
import { registerUser } from '../services/authService.js';

function Register() {
  const navigate = useNavigate();
  const { login } = useAuth();
  const [form, setForm] = useState({ full_name: '', email: '', password: '' });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');
  const [fieldErrors, setFieldErrors] = useState({});
  const passwordHint = 'Mínimo 8 caracteres, con una mayúscula, un número y un símbolo.';

  const handleChange = (event) => {
    const { name, value } = event.target;
    setForm((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    setError('');
    setFieldErrors({});
    setLoading(true);
    try {
      const response = await registerUser(form);
      login(response.token, response.user);
      navigate('/analyze');
    } catch (err) {
      setFieldErrors(err.response?.data?.errors || {});
      setError(err.response?.data?.message || err.message || 'No se pudo registrar.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <section className="auth-page">
      <article className="auth-illustration">
        <p className="auth-kicker">¿Listo para crear tu cuenta?</p>
        <h2>Regístrate con tus datos personales para acceder a la plataforma.</h2>
        <ul className="auth-sample-list">
          <li>
            <span>Nombre completo</span>
            <strong>Ejemplo: Ana Martínez López</strong>
          </li>
          <li>
            <span>Correo empresarial</span>
            <strong>Ejemplo: ana.martinez@tuempresa.com</strong>
          </li>
          <li>
            <span>Contraseña segura</span>
            <strong>Ejemplo: TuClaveFuerte!2025</strong>
          </li>
        </ul>
        <p className="auth-sample-foot">
          Usa tus datos reales y una contraseña segura para proteger tu cuenta.
        </p>
      </article>

      <article className="auth-layout card">
        <h1 className="page-title">Crear cuenta</h1>
        <form onSubmit={handleSubmit} className="form-stack" style={{ display: 'flex', flexDirection: 'column', gap: '1rem' }}>
          <div className="form-group">
            <label htmlFor="full_name">Nombre completo</label>
            <input
              id="full_name"
              name="full_name"
              placeholder="Ej. Valeria Torres Andrade"
              value={form.full_name}
              onChange={handleChange}
              required
              aria-invalid={Boolean(fieldErrors.full_name)}
            />
            {fieldErrors.full_name && <p className="form-error">{fieldErrors.full_name}</p>}
          </div>
          <div className="form-group">
            <label htmlFor="email">Correo</label>
            <input
              id="email"
              name="email"
              type="email"
              placeholder="Ej. valeria.torres@emociones.ai"
              value={form.email}
              onChange={handleChange}
              required
              aria-invalid={Boolean(fieldErrors.email)}
            />
            {fieldErrors.email && <p className="form-error">{fieldErrors.email}</p>}
          </div>
          <div className="form-group">
            <label htmlFor="password">Contraseña</label>
            <input
              id="password"
              name="password"
              type="password"
              placeholder="Ej. EmoRadar!2025"
              value={form.password}
              onChange={handleChange}
              required
              minLength={8}
              title={passwordHint}
              autoComplete="new-password"
              aria-invalid={Boolean(fieldErrors.password)}
            />
            <small className="form-hint">{passwordHint}</small>
            {fieldErrors.password && <p className="form-error">{fieldErrors.password}</p>}
          </div>
          {error && <p style={{ color: 'crimson' }}>{error}</p>}
          <button className="primary-btn" type="submit" disabled={loading}>
            {loading ? 'Creando...' : 'Registrarme'}
          </button>
          <p style={{ textAlign: 'center', marginTop: '0.5rem' }}>
            ¿Ya tienes cuenta? <Link to="/login">Inicia sesión</Link>
          </p>
        </form>
      </article>
    </section>
  );
}

export default Register;
