import { useCallback, useEffect, useMemo, useState } from 'react';

import MediaResultCard from '../components/MediaResultCard.jsx';
import useSnapshotPreview from '../hooks/useSnapshotPreview.js';
import { fetchMediaHistory, fetchModelMetadata } from '../services/mediaService.js';

const SOURCE_FILTERS = [
  { key: 'all', label: 'Todos los registros' },
  { key: 'camera', label: 'Capturado desde la cámara' },
  { key: 'image', label: 'Analizado de imagen' },
  { key: 'video', label: 'Analizado de video' },
];

const SOURCE_LABELS = {
  all: 'Todos los registros',
  camera: 'Capturado desde la cámara',
  image: 'Analizado de imagen',
  video: 'Analizado de video',
  'image-upload': 'Imagen subida',
  'video-upload': 'Video subido',
  webcam: 'Webcam (clip)',
  'webcam-live': 'Webcam en vivo',
};

const MEDIA_LABELS = {
  all: 'Todos los medios',
  image: 'Imágenes',
  video: 'Videos',
  'live-session': 'Sesiones en vivo',
};

function EmotionPreviewFilter({ label, total, snapshot, isActive, onSelect }) {
  const { previewUrl, fallbackUrl, state } = useSnapshotPreview(snapshot);
  const imageUrl = previewUrl || fallbackUrl;
  const placeholderLetter = label ? label.substring(0, 1).toUpperCase() : '?';

  return (
    <button
      type="button"
      className={`emotion-preview-card ${isActive ? 'is-active' : ''}`}
      onClick={onSelect}
    >
      <span
        className={`emotion-preview-thumb ${state === 'loading' ? 'is-loading' : ''} ${imageUrl ? '' : 'is-placeholder'}`}
        style={imageUrl ? { backgroundImage: `url(${imageUrl})` } : undefined}
      >
        {!imageUrl && placeholderLetter}
      </span>
      <span className="emotion-preview-label">
        <strong>{label}</strong>
        <small>{total} registros</small>
      </span>
    </button>
  );
}

function Dashboard() {
  const [overviewPool, setOverviewPool] = useState([]);
  const [latestResults, setLatestResults] = useState([]);
  const [history, setHistory] = useState([]);
  const [filters, setFilters] = useState({ sourceType: 'all', emotion: 'all', mediaType: 'all' });
  const [availableEmotions, setAvailableEmotions] = useState([]);
  const [availableSources, setAvailableSources] = useState([]);
  const [modelLabels, setModelLabels] = useState([]);
  const [historyLoading, setHistoryLoading] = useState(false);
  const [error, setError] = useState('');

  const handleFilterChange = (key) => (event) => {
    const value = typeof event === 'string' ? event : event?.target?.value;
    setFilters((prev) => ({ ...prev, [key]: value }));
  };

  const loadOverview = useCallback(async () => {
    try {
      const response = await fetchMediaHistory({ limit: 30 });
      const items = response.items || [];
      setOverviewPool(items);
      setLatestResults(items.slice(0, 4));
      setAvailableEmotions(response.filters?.available_emotions || []);
      setAvailableSources(response.filters?.available_sources || []);
    } catch (loadError) {
      setError(loadError.response?.data?.message || 'No se pudo cargar el dashboard de emociones.');
    }
  }, []);

  const loadMetadata = useCallback(async () => {
    try {
      const metadata = await fetchModelMetadata();
      setModelLabels(metadata.labels || []);
    } catch (metadataError) {
      console.error('No se pudo obtener etiquetas del modelo', metadataError);
    }
  }, []);

  const loadHistory = useCallback(async () => {
    setHistoryLoading(true);
    setError('');
    try {
      const response = await fetchMediaHistory({
        limit: 18,
        sourceType: filters.sourceType,
        emotion: filters.emotion,
        mediaType: filters.mediaType,
      });
      setHistory(response.items || []);
      setAvailableEmotions(response.filters?.available_emotions || []);
      setAvailableSources(response.filters?.available_sources || []);
    } catch (loadError) {
      setError(loadError.response?.data?.message || 'No se pudo filtrar el historial.');
    } finally {
      setHistoryLoading(false);
    }
  }, [filters]);

  useEffect(() => {
    loadOverview();
    loadMetadata();
  }, [loadOverview, loadMetadata]);

  useEffect(() => {
    loadHistory();
  }, [loadHistory]);

  const stats = useMemo(() => {
    const total = overviewPool.length;
    const emotionCounter = overviewPool.reduce((acc, item) => {
      if (!item.dominant_emotion) {
        return acc;
      }
      const key = item.dominant_emotion;
      acc[key] = (acc[key] || 0) + 1;
      return acc;
    }, {});
    const sortedEmotions = Object.entries(emotionCounter).sort((a, b) => b[1] - a[1]);
    const topEmotion = sortedEmotions[0];

    const sourceCounter = overviewPool.reduce(
      (acc, item) => {
        const key = item.source_type || 'otros';
        acc[key] = (acc[key] || 0) + 1;
        return acc;
      },
      {}
    );

    return {
      total,
      topEmotionLabel: topEmotion ? `${topEmotion[0]} (${topEmotion[1]})` : 'Sin registros',
      topEmotionCount: topEmotion?.[1] || 0,
      sourceCounter,
      lastTimestamp: overviewPool[0]?.created_at || null,
      emotionCounter,
    };
  }, [overviewPool]);

  const emotionSnapshots = useMemo(() => {
    const snapshotMap = new Map();
    [...overviewPool, ...history].forEach((item) => {
      if (!item?.dominant_emotion || !item.snapshot_url) {
        return;
      }
      if (!snapshotMap.has(item.dominant_emotion)) {
        snapshotMap.set(item.dominant_emotion, item.snapshot_url);
      }
    });
    return snapshotMap;
  }, [overviewPool, history]);

  const sourceOptions = availableSources.length > 0 ? availableSources : Object.keys(SOURCE_LABELS).filter((key) => key !== 'all');
  const sourceBadges = Object.entries(stats.sourceCounter || {});
  const historyBadgeLabel = historyLoading ? 'Actualizando…' : `${history.length} resultados`;
  const emotionBadges = (modelLabels.length > 0
    ? modelLabels
    : Array.from(new Set([...availableEmotions, ...Object.keys(stats.emotionCounter || {})])))
    .filter(Boolean)
    .map((label) => ({
      label,
      total: stats.emotionCounter?.[label] || 0,
      snapshot: emotionSnapshots.get(label) || null,
    }));

  return (
    <section className="app-container emotion-dashboard">
      <div className="emotion-dashboard-hero">
        <div>
          <p className="page-subtitle" style={{ color: 'rgba(248, 250, 252, 0.7)' }}>Dashboard emociones</p>
          <h1>Observa cada emoción capturada por la IA</h1>
          <p style={{ margin: '0.5rem 0 0', maxWidth: '640px', color: 'rgba(248, 250, 252, 0.8)' }}>
            Visualiza los resultados más recientes provenientes de imágenes, videos y sesiones en vivo. Filtra por origen o emoción para encontrar exactamente lo que necesitas.
          </p>
        </div>
        <div className="emotion-dashboard-actions">
          <div className="emotion-dashboard-stats">
            <div className="emotion-stat-card">
              <p>Emoción dominante</p>
              <strong>{stats.topEmotionLabel}</strong>
            </div>
            <div className="emotion-stat-card">
              <p>Última captura</p>
              <strong>{stats.lastTimestamp ? new Date(stats.lastTimestamp).toLocaleTimeString() : 'N/D'}</strong>
            </div>
            <div className="emotion-stat-card">
              <p>Total de registros</p>
              <strong>{stats.total}</strong>
            </div>
          </div>
          <div className="emotion-filters">
            <div className="emotion-source-filter">
              {['all', ...sourceOptions].map((key) => (
                <button
                  type="button"
                  key={key}
                  className={filters.sourceType === key ? 'is-active' : ''}
                  onClick={() => setFilters((prev) => ({ ...prev, sourceType: key }))}
                >
                  {SOURCE_LABELS[key] || key}
                </button>
              ))}
            </div>
            <label>
              Tipo de medio
              <select value={filters.mediaType} onChange={handleFilterChange('mediaType')}>
                {Object.entries(MEDIA_LABELS).map(([key, label]) => (
                  <option key={key} value={key}>
                    {label}
                  </option>
                ))}
              </select>
            </label>
          </div>
          <div className="emotion-filter-preview">
            <p className="emotion-filter-preview-title">Vista previa por emoción</p>
            <div className="emotion-preview-grid">
              <button
                type="button"
                className={`emotion-preview-card is-compact ${filters.emotion === 'all' ? 'is-active' : ''}`}
                onClick={() => setFilters((prev) => ({ ...prev, emotion: 'all' }))}
              >
                <span className="emotion-preview-thumb is-placeholder">Todas</span>
                <span className="emotion-preview-label">
                  <strong>Todo el set</strong>
                  <small>{stats.total} capturas</small>
                </span>
              </button>
              {emotionBadges.map(({ label, total, snapshot }) => (
                <EmotionPreviewFilter
                  key={label}
                  label={label}
                  total={total}
                  snapshot={snapshot}
                  isActive={filters.emotion === label}
                  onSelect={() => setFilters((prev) => ({ ...prev, emotion: label }))}
                />
              ))}
            </div>
          </div>
        </div>
        {sourceBadges.length > 0 && (
          <div className="emotion-source-chips">
            {sourceBadges.map(([key, qty]) => (
              <span key={key} className="emotion-chip emotion-chip-muted">
                {SOURCE_LABELS[key] || key}
                <strong>{qty}</strong>
              </span>
            ))}
          </div>
        )}
      </div>

      <div className="emotion-panel card">
        <div className="emotion-panel-header">
          <div>
            <p className="emotion-panel-kicker">Pulso inmediato</p>
            <h2 className="emotion-section-title">Últimos resultados</h2>
          </div>
          <span className="emotion-chip chip-live">En vivo</span>
        </div>
        {latestResults.length === 0 ? (
          <p className="emotion-dashboard-empty">Aún no hay registros recientes.</p>
        ) : (
          <div className="emotion-latest-grid">
            {latestResults.map((item) => (
              <MediaResultCard key={`latest-${item.id}`} item={item} />
            ))}
          </div>
        )}
      </div>

      <div className="emotion-panel card">
        <div className="emotion-panel-header">
          <div>
            <p className="emotion-panel-kicker">Historial almacenado</p>
            <h2 className="emotion-section-title">Analíticas filtradas</h2>
          </div>
          <span className="emotion-chip">{historyBadgeLabel}</span>
        </div>
        {error && <p className="media-error">{error}</p>}
        {historyLoading ? (
          <p className="emotion-dashboard-empty">Cargando historial...</p>
        ) : history.length === 0 ? (
          <p className="emotion-dashboard-empty">No hay coincidencias con los filtros aplicados.</p>
        ) : (
          <div className="emotion-history-grid">
            {history.map((item) => (
              <MediaResultCard key={`history-${item.id}`} item={item} />
            ))}
          </div>
        )}
      </div>
    </section>
  );
}

export default Dashboard;
