import { useEffect, useState } from 'react';
import { Bar, BarChart, ResponsiveContainer, Tooltip, XAxis, YAxis } from 'recharts';

import ResultCard from '../components/ResultCard.jsx';
import StatCard from '../components/StatCard.jsx';
import useAuth from '../hooks/useAuth.js';
import { fetchProfile } from '../services/authService.js';
import { fetchDashboard } from '../services/analysisService.js';

function Dashboard() {
  const { user, updateUser } = useAuth();
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [stats, setStats] = useState({ totals: { users: 0, analyses: 0 }, sentiment_breakdown: {}, recent_results: [] });

  useEffect(() => {
    const loadData = async () => {
      try {
        const [profileData, dashboardData] = await Promise.all([fetchProfile(), fetchDashboard()]);
        updateUser(profileData);
        setStats(dashboardData);
      } catch (err) {
        setError(err.response?.data?.message || 'No se pudo cargar el dashboard.');
      } finally {
        setLoading(false);
      }
    };
    loadData();
  }, [updateUser]);

  const chartData = Object.entries(stats.sentiment_breakdown || {}).map(([label, value]) => ({ label, value }));

  return (
    <section className="app-container">
      <h1 className="page-title">Hola, {user?.full_name || 'Analista'}</h1>
      {error && <p style={{ color: 'crimson' }}>{error}</p>}
      {loading ? (
        <p>Cargando métricas...</p>
      ) : (
        <>
          <div className="dashboard-grid">
            <StatCard label="Usuarios" value={stats.totals.users} />
            <StatCard label="Análisis totales" value={stats.totals.analyses} />
            <StatCard label="Sentimientos registrados" value={chartData.length} />
          </div>

          {chartData.length > 0 && (
            <div className="card" style={{ height: 320 }}>
              <h3>Distribución de sentimientos</h3>
              <ResponsiveContainer width="100%" height="85%">
                <BarChart data={chartData}>
                  <XAxis dataKey="label" />
                  <YAxis allowDecimals={false} />
                  <Tooltip />
                  <Bar dataKey="value" fill="#4f46e5" radius={[8, 8, 0, 0]} />
                </BarChart>
              </ResponsiveContainer>
            </div>
          )}

          <div style={{ marginTop: '2rem' }}>
            <h2>Resultados recientes</h2>
            {stats.recent_results.length === 0 ? (
              <p>No hay análisis registrados aún.</p>
            ) : (
              <div className="result-history">
                {stats.recent_results.map((item) => (
                  <ResultCard key={item.id} result={item} />
                ))}
              </div>
            )}
          </div>

          <div style={{ marginTop: '2rem' }}>
            <h2>Tabla de registros</h2>
            {stats.recent_results.length === 0 ? (
              <p>No hay datos en la tabla.</p>
            ) : (
              <div className="card table-wrapper">
                <table className="data-table">
                  <thead>
                    <tr>
                      <th>ID</th>
                      <th>Sentimiento</th>
                      <th>Polaridad</th>
                      <th>Subjetividad</th>
                      <th>Fecha</th>
                    </tr>
                  </thead>
                  <tbody>
                    {stats.recent_results.map((item) => (
                      <tr key={`row-${item.id}`}>
                        <td>{item.id}</td>
                        <td style={{ textTransform: 'capitalize' }}>{item.sentiment_label}</td>
                        <td>{item.polarity}</td>
                        <td>{item.subjectivity}</td>
                        <td>{new Date(item.created_at).toLocaleString()}</td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              </div>
            )}
          </div>
        </>
      )}
    </section>
  );
}

export default Dashboard;
