import { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';

import useAuth from '../hooks/useAuth.js';
import { registerUser } from '../services/authService.js';

function Register() {
  const navigate = useNavigate();
  const { login } = useAuth();
  const [form, setForm] = useState({ full_name: '', email: '', password: '' });
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState('');

  const handleChange = (event) => {
    const { name, value } = event.target;
    setForm((prev) => ({ ...prev, [name]: value }));
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    setError('');
    setLoading(true);
    try {
      const response = await registerUser(form);
      login(response.token, response.user);
      navigate('/dashboard');
    } catch (err) {
      setError(err.response?.data?.message || 'No se pudo registrar.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <section className="auth-layout card">
      <h1 className="page-title">Crear cuenta</h1>
      <form onSubmit={handleSubmit} className="form-stack" style={{ display: 'flex', flexDirection: 'column', gap: '1rem' }}>
        <div className="form-group">
          <label htmlFor="full_name">Nombre completo</label>
          <input id="full_name" name="full_name" value={form.full_name} onChange={handleChange} required />
        </div>
        <div className="form-group">
          <label htmlFor="email">Correo</label>
          <input id="email" name="email" type="email" value={form.email} onChange={handleChange} required />
        </div>
        <div className="form-group">
          <label htmlFor="password">Contraseña</label>
          <input id="password" name="password" type="password" value={form.password} onChange={handleChange} required />
        </div>
        {error && <p style={{ color: 'crimson' }}>{error}</p>}
        <button className="primary-btn" type="submit" disabled={loading}>
          {loading ? 'Creando...' : 'Registrarme'}
        </button>
        <p style={{ textAlign: 'center', marginTop: '0.5rem' }}>
          ¿Ya tienes cuenta? <Link to="/login">Inicia sesión</Link>
        </p>
      </form>
    </section>
  );
}

export default Register;
